  {title: "Verbis Diablo", episode_number: "S2, Ep2", description: "\nFerdinand Lyle reveals Verbis Diablo's disturbing backstory.    ", season_id: 2385, series_id: 8, season_episode_number: nil, air_date: "2015-05-10", seasonno: nil, url: "/title/tt3780304/?ref_=ttep_ep2"},
