  {title: "XY", episode_number: "S8, Ep1", description: "\nA call from a mysterious stranger ignites an explosive chain of events; the story is told from Castle's perspective as he searches for Beckett.    ", season_id: 2537, series_id: 37, season_episode_number: nil, air_date: "2015-09-21", seasonno: nil, url: "/title/tt4667522/?ref_=ttep_ep1"},
