  {title: "Cool Boys", episode_number: "S8, Ep6", description: "\nWhile Beckett is out of town, Castle becomes involved with Detective Slaughter in a case where a hi-tech chip has been stolen and one of Slaughter's informants is murdered.    ", season_id: 2537, series_id: 37, season_episode_number: nil, air_date: "2015-11-09", seasonno: nil, url: "/title/tt5089670/?ref_=ttep_ep6"},
  {title: "The Last Seduction", episode_number: "S8, Ep7", description: "\nIn order to hunt down the brutal murderer of a grifting seducer, Castle and Beckett must investigate New York's elite. Meanwhile, Castle plans to surprise Beckett on their one-year wedding anniversary.    ", season_id: 2537, series_id: 37, season_episode_number: nil, air_date: "2015-11-16", seasonno: nil, url: "/title/tt5030300/?ref_=ttep_ep7"},
