  {title: "Pilot", episode_number: "S1, Ep1", description: "\nTwenty-four-year-old Kara Zor-El, cousin of Superman, who was taken in by the Danvers family when she was 12 after being sent away from Krypton, must learn to embrace her powers after previously hiding them.    ", season_id: 2610, series_id: 50, season_episode_number: nil, air_date: "2015-10-26", seasonno: nil, url: "/title/tt4525842/?ref_=ttep_ep1"},
  {title: "Stronger Together", episode_number: "S1, Ep2", description: "\nKara must put doubts aside as she tries to apprehend an escapee from the Kryptonian prison; Cat pressures James for an interview with Supergirl.    ", season_id: 2610, series_id: 50, season_episode_number: nil, air_date: "2015-11-02", seasonno: nil, url: "/title/tt4550416/?ref_=ttep_ep2"},
  {title: "Fight or Flight", episode_number: "S1, Ep3", description: "\nSupergirl's powers are put to the test when one of Superman's most formidable enemies, Reactron, shows up in National City to take her down. In the meantime, Cat makes plans to run an expose on Supergirl.    ", season_id: 2610, series_id: 50, season_episode_number: nil, air_date: "2015-11-09", seasonno: nil, url: "/title/tt4603116/?ref_=ttep_ep3"},
